import './CurrentVideo.scss';
import VideoInfo from '../VideoInfo/VideoInfo';
import Form from '../Form/Form';
import Comments from '../Comments/Comments';
import moment from 'moment';

function CurrentVideo() {
  let videoObject = {
    id: '1af0jruup5gu',
    title: 'BMX Rampage: 2018 Highlights',
    channel: 'Red Cow',
    image: 'https://i.imgur.com/l2Xfgpl.jpg',
    description:
      'On a gusty day in Southern Utah, a group of 25 daring mountain bikers blew the doors off what is possible on two wheels, unleashing some of the biggest moments the sport has ever seen. While mother nature only allowed for one full run before the conditions made it impossible to ride, that was all that was needed for event veteran Kyle Strait, who won the event for the second time -- eight years after his first Red Cow Rampage title',
    views: '1,001,023',
    likes: '110,985',
    duration: '4:01',
    video: 'https://project-2-api.herokuapp.com/stream',
    timestamp: 1545162149000,
    comments: [
      {
        name: 'Micheal Lyons',
        comment:
          'They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of acconcert I have EVER witnessed.',
        id: '1ab6d9f6-da38-456e-9b09-ab0acd9ce818',
        likes: 0,
        timestamp: 1545162149000,
      },
      {
        name: 'Gary Wong',
        comment:
          'Every time I see him shred I feel so motivated to get off my couch and hop on my board. He’s so talented! I wish I can ride like him one day so I can really enjoy myself!',
        id: 'cc6f173d-9e9d-4501-918d-bc11f15a8e14',
        likes: 0,
        timestamp: 1544595784046,
      },
      {
        name: 'Theodore Duncan',
        comment:
          'How can someone be so good!!! You can tell he lives for this and loves to do it every day. Everytime I see him I feel instantly happy! He’s definitely my favorite ever!',
        id: '993f950f-df99-48e7-bd1e-d95003cc98f1',
        likes: 0,
        timestamp: 1542262984046,
      },
    ],
  };

  let commentsArray = videoObject.comments;
  return (
    <section className="video">
      <figure className="video__figure">
        <video className="video__figure--video" width="100%" controls poster="https://i.imgur.com/l2Xfgpl.jpg"></video>
      </figure>
      <VideoInfo
        title={videoObject.title}
        channel={videoObject.channel}
        timestamp={videoObject.timestamp}
        views={videoObject.views}
        likes={videoObject.likes}
        description={videoObject.description}
      />
      <Form />
      {commentsArray.map((comment) => {
        comment.image = 'https://loremflickr.com/48/48';
        comment.date = moment.unix(comment.timestamp / 1000).fromNow();
        console.log(comment.image);
        return <Comments image={comment.image} name={comment.name} date={comment.date} comment={comment.comment} />;
      })}
    </section>
  );
}

export default CurrentVideo;
