import { Component } from 'react';
import './CurrentVideo.scss';
import VideoInfo from '../VideoInfo/VideoInfo';
import Form from '../Form/Form';
import Comments from '../Comments/Comments';
import moment from 'moment';
import Video from '../Video/Video';
import videoDetails from '../../data/video-details.json';

console.log(videoDetails[0].comments);

export default class CurrentVideo extends Component {
  commentsArray = videoDetails[0].comments;
  state = {
    videos: videoDetails,
    defaultVideo: 0,
  };

  render() {
    return (
      <section className="currentVideo">
        <Video image={videoDetails[this.state.defaultVideo].image} />
        <button onClick={() => this.setState({ defaultVideo: this.state.defaultVideo + 1 })}>Click Me</button>
        <VideoInfo
          title={videoDetails[this.state.defaultVideo].title}
          channel={videoDetails[this.state.defaultVideo].channel}
          timestamp={videoDetails[this.state.defaultVideo].timestamp}
          views={videoDetails[this.state.defaultVideo].views}
          likes={videoDetails[this.state.defaultVideo].likes}
          description={videoDetails[this.state.defaultVideo].description}
        />
        <Form comments={videoDetails[this.state.defaultVideo].comments} />
        {this.commentsArray.map((comment) => {
          comment.image = 'https://loremflickr.com/48/48';
          comment.date = moment.unix(comment.timestamp / 1000).fromNow();
          return <Comments image={comment.image} name={comment.name} date={comment.date} comment={comment.comment} />;
        })}
      </section>
    );
  }
}
